mapper.delete:
DELETE FROM b_filedetail
WHERE atch_file_id = #{atch_file_id} 
AND file_sn = #{file_sn}

mapper.update:

UPDATE b_filedetail
SET acth_file_id = #{atch_file_id}, file_sn = #{file_sn}
WHERE acth_file_id = #{oldKey} 
AND file_sn = #{oldSn}

UPDATE b_filemaster
SET atch_file_id = #{newKey}
WHERE atch_file_id = #{atch_file_id}

UPDATE b_board
SET atch_file_id = #{atch_file_id}
WHERE num = #{num}
